import { useState } from "react";

function Square({ value, onClick }) {
  return <button className="square">{value}</button>;
}

export default function Board() {
  const [value, setValue] = useState(Array(9).fill(null));

  const onSquareClick = (i) => {
    const squares = value.slice();
    squares[i] = "X";
    setValue(squares);
  };
  return (
    <>
      <div className="board-row">
        <Square value={value[0]} onClick={onSquareClick(0)} />
        <Square value={value[1]} onClick={onSquareClick(1)} />
        <Square value={value[2]} onClick={onSquareClick(2)} />
      </div>
      <div className="board-row">
        <Square value={value[3]} onClick={onSquareClick(3)} />
        <Square value={value[4]} onClick={onSquareClick(4)} />
        <Square value={value[5]} onClick={onSquareClick(5)} />
      </div>
      <div className="board-row">
        <Square value={value[6]} onClick={onSquareClick(6)} />
        <Square value={value[7]} onClick={onSquareClick(7)} />
        <Square value={value[8]} onClick={onSquareClick(8)} />
      </div>
    </>
  );
}
